// 1. Import the React and ReactDom libraries
import React from 'react';
// understands what a component is and how multiple components work together
import ReactDOM from 'react-dom/client';
// knows how to get a component to show in the browser, specifically for wed dev


// 2. Get a reference to the div with ID root
const el = document.getElementById('root')

// 3. Tell React to take controls of that element 
const root = ReactDOM.createRoot(el); 

// 4. Create a component 
function App() {
    const name = 'Mack';
    const age = 32;
    return <h1> Hi, my name is {name} and my age is {age} </h1>
}
// 5. Show the component on the screen 
root.render(<App />)
